mapper.select:
select

from menu_info
  
     and menu_name like concat('%', #{menuName}, '%')
     and parent_id = #{parentId}
     and order_num = #{orderNum}
     and url = #{url}
     and target = #{target}
     and menu_type = #{menuType}
     and visible = #{visible}
     and perms = #{perms}
     and icon = #{icon}

order by parent_id, order_num

select

from menu_info
where menu_name=#{menuName} and parent_id = #{parentId}

mapper.update:
update menu_info
menu_name = #{menuName}, parent_id = #{parentId}, order_num = #{orderNum}, url = #{url}, target = #{target}, menu_type = #{menuType}, visible = #{visible}, perms = #{perms}, icon = #{icon}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where menu_id = #{menuId}
